import React from 'react';
import { useState } from 'react';

const MyTabsComponent = () => {
  const [isActive, setIsActive] = useState('');
  const getOff = () => {
    setIsActive('-active');
  };
  return (
    <div className="tabs">
      <button className={`btn${isActive}`} onClick={getOff()}></button>
    </div>
  );
};

export default MyTabsComponent;
