import React from 'react';
import { useState } from 'react';

const MyTabsComponent = () => {
  const [isActive, setIsActive] = useState('');
  const [classDiv, setClassDiv] = useState('tabs');
  const renderView = () => {
    setClassDiv['views'];
  };
  const getOff = () => {
    setIsActive('-active');
  };
  return (
    <div className={classDiv} onClick={renderView}>
      <div className="view">
        <button className={`btn${isActive}`} onClick={getOff}>
          Section Tittle 1
        </button>
      </div>
      <button className={`btn${isActive}`} onClick={getOff}>
        Section Tittle 2
      </button>
      <button className={`btn${isActive}`} onClick={getOff}>
        Section Tittle 3
      </button>
    </div>
  );
};

export default MyTabsComponent;
